import { defineStore } from 'pinia';

type {clipboard}Store = { 
  count: number;
} 

export const use{clipboard}Store = defineStore('{clipboard}', {
  state: (): {clipboard}Store => ({ 
    count: 0,
  }), 
  getters: { 
    getCount(): number { 
      return this.count; 
    }
  },
  actions: { 
    increment() {
      this.count++; 
    }, 
  } 
});

export function use{clipboard}() {
  const {clipboard} = ref(null);

  // Composable logic

  return { {clipboard} };
}
